import React from "react";
import NewsCard from "./newsCard";
import "./news.css";
import Spinner from "./spinner";
import PropTypes from "prop-types";
import { useState } from "react";
import { useEffect } from "react";



export default function Search(props){
 const[articles, setArticles] =useState([]);
 const[loading, setLoadingScreen]=useState('true');
 const[page, setPage]= useState(1);
 const[totalResults , setTotalResults] = useState(0);

 const updateNews= async() => {
    setLoading('true');
    let url = `https://newsapi.org/v2/top-headlines?q=${page}&sortBy=publishedAt&apiKey=b78e87013150454abec15a569230b581&pageSize=${this.props.pageSize}&page=${this.state.page}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    setLoadingScreen('false');
    setArticles(parsedData.articles);
    setTotalResults(parsedData.totalResults);

  }
   useEffect(()=> {
    setLoading('true');
    let url = `https://newsapi.org/v2/top-headlines?q=${page}&sortBy=publishedAt&apiKey=b78e87013150454abec15a569230b581&pageSize=${this.props.pageSize}&page=1`;
    let data =  fetch(url);
    let parsedData = data.json();
    setLoadingScreen('false');
    setArticles(parsedData.articles);
    setTotalResults(parsedData.totalResults);

   });

  const loadNext = async () => {
    setPage(page+1);
    updateNews();
  };
  const loadPrevious = async () => {
    setPage(page-1);
    updateNews();
  };

    return (
      <>
        <div className="text-left mx-2">
          <small style={{ color: "grey" }}>Newsy &#62; Search</small>
        </div>
        <div className="news_screen">
          <div className="news_container">
            {!loading &&
              articles.map((element) => {
                return (
                  <NewsCard
                    title={element.title}
                    description={element.description}
                    imgSrc={element.urlToImage}
                    key={element.url}
                    newsUrl={element.url}
                    source={element.source.name}
                    author={element.author}
                    publishedDate={element.publishedAt}
                  />
                );
              })}
          </div>
        </div>
        {loading && <Spinner />}
        <div
          className="btn-container"
          style={{
            justifyContent: page === 1 ? "flex-end" : "",
            display: loading ? "none" : "",
          }}
        >
          <button
            type="button"
            className="btn btn-info"
            style={{ display: page === 1 ? "none" : "" }}
            onClick={loadPrevious}
          >
            &#8592;Previous
          </button>
          <button
            type="button"
            className="btn btn-info"
            style={{
              display:
                page * 15 >= totalResults ||
                props.pageSize * page >= 90
                  ? "none"
                  : "",
            }}
            onClick={loadNext}
          >
            Next&#8594;
          </button>
        </div>
      </>
    );

}
Search.defaultProps ={
  pageSize: 15
}
Search.PropTypes = {
  pageSize: PropTypes.number
}
