import React, { Component } from 'react';
import NavBar from './components/navBar';
import News from './components/news';
import { Route, Routes,BrowserRouter as Router} from 'react-router-dom'
import Search from './components/search';
import LoadingBar from 'react-top-loading-bar';

export default class App extends Component{
  state = {
    progress: 0
  }
  setProgress(progress){
    this.setState( 
      {
        progress: progress
      }
    )
  }
  render(){
    return(
      <>
      <Router>

      <LoadingBar
        color='#f11946'
        progress={this.state.progress}

      />
      <NavBar/>
      <Routes>
        <Route exact strict path = "/" element ={<News setProgress = {setProgress} key = "home" type = "home"/>} />
        <Route exact strict path = "business" element ={<News setProgress = {setProgress} key ="business" category ="business"/>} />
        <Route exact strict path = "entertainment" element ={<News setProgress = {setProgress} key ="entertainment" category ="entertainment"/>} />
        <Route exact strict path = "general" element ={<News setProgress = {setProgress} key ="general" category ="general"/>} />
        <Route exact strict path = "health" element ={<News setProgress = {setProgress} key ="health" category ="health"/>} />
        <Route exact strict path = "science" element ={<News setProgress = {setProgress} key ="science" category ="science"/>} />
        <Route exact strict path = "sport" element ={<News setProgress = {setProgress} key ="sport" category ="sport"/>} />
        <Route exact strict path = "technology" element ={<News setProgress = {setProgress} key ="technology" category ="technology"/>} />
        <Route exact strict path="/search/:query" element={<Search key= "search"/>}/>
      </Routes>
      </Router>
      </>
    );
  }
}
