import React, { useState } from "react";
import NewsCard from "./newsCard";
import "./news.css";
import Spinner from "./spinner";
import { useEffect } from "react";


export default function  News(props) {
 const[articles, setArticles]= useState([]);
 const[loading, setLoading]= useState(true);
 const[totalResults, setResults] =useState(0);
  const loadNews = async()=> {
    props.setProgress(10);
    setLoading(true);
    let url = `https://newsapi.org/v2/everything?q=${this.props.query}&sortBy=publishedAt&apiKey=${this.props.apiKey}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    setLoading(true);
    setArticles(parsedData.articles);
    setResults(parsedData.totalResults);
    props.setProgress(100);
  }
  useEffect({
    loadNews
  }, []);




    return (
      <>
      <div className="text-left mx-2">
        <small style= {{color: 'grey'}}>
          Newsy &#62; Search

        </small>
      </div>
      {loading && <Spinner/>}



    <div className="row">
            {
              articles.map((element) => {
                return (

                  <NewsCard
                    title={element.title}
                    description={element.description}
                    imgSrc={element.urlToImage}
                    key={element.url+element.title+element.description+element.publishedAt+element.author+Math.random()}
                    newsUrl={element.url}
                    source={element.source.name}
                    author={element.author}
                    publishedDate={element.publishedAt}
                  />
                );
              })}
               </div>



       </>
    );
  }
News.defaultProps = {

    query: "usa",

  };
