import React, { Component } from "react";
import NewsCard from "./newsCard";
import "./news.css";
import Spinner from "./spinner";
import PropTypes from "prop-types";
import InfiniteScroll from 'react-infinite-scroll-component';
export default class News extends Component {
  static defaultProps = {
    pageSize: 15,
    category: "general",
    country: "",
    language: "en",
  };
  static propTypes = {
    pageSize: PropTypes.number,
    category: PropTypes.string,
    country: PropTypes.string,
    language: PropTypes.string,
  };
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      totalResults: 0,
    };
  }

  async updateNews() {
    this.setState({
      loading: true,
    });
    let url = `https://newsapi.org/v2/top-headlines?category=${this.props.category}&country=${this.props.country}&language=${this.props.language}&apiKey=b78e87013150454abec15a569230b581&pageSize=${this.props.pageSize}&page=${this.state.page}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      loading: false,
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
    });
  }
  async componentDidMount() {
    this.setState({
      loading: true,
    });
    let url = `https://newsapi.org/v2/top-headlines?category=${this.props.category}&country=${this.props.country}&language=${this.props.language}&apiKey=b78e87013150454abec15a569230b581&pageSize=${this.props.pageSize}&page=1`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      loading: false,
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
    });
  }

  loadNext = async () => {
    this.setState({
      page: this.state.page + 1,
    });
    this.updateNews();
  };
  loadPrevious = async () => {
    this.setState({
      page: this.state.page - 1,
    });
    this.updateNews();
  };
  render() {
    return (
      <>
      <div className="text-left mx-2">
        <small style= {{color: 'grey'}}>
          Newsy &#62; {this.props.category.charAt(0).toUpperCase()+ this.props.category.slice(1)}
        </small>
      </div>
      <InfiniteScroll
    dataLength={this.state.items.length}
    next={this.fetchMoreData}
    style={{ display: 'flex', flexDirection: 'column-reverse' }} //To put endMessage and loader to the top.
    inverse={true} //
    hasMore={true}
    loader={<h4>Loading...</h4>}
    scrollableTarget="scrollableDiv"
  >
        <div className="news_screen">


     <div className="news_container">
            {!this.state.loading &&
              this.state.articles.map((element) => {
                return (
                  <NewsCard
                    title={element.title}
                    description={element.description}
                    imgSrc={element.urlToImage}
                    key={element.url}
                    newsUrl={element.url}
                    source={element.source.name}
                    author={element.author}
                    publishedDate={element.publishedAt}
                  />
                );
              })}
          </div>

        </div>
        </InfiniteScroll>
        {this.state.loading && <Spinner />}
        <div
          className="btn-container"
          style={{
            justifyContent: this.state.page === 1 ? "flex-end" : "",
            display: this.state.loading ? "none" : "",
          }}
        >
          <button
            type="button"
            className="btn btn-info"
            style={{ display: this.state.page === 1 ? "none" : "" }}
            onClick={this.loadPrevious}
          >
            &#8592;Previous
          </button>
          <button
            type="button"
            className="btn btn-info"
            style={{
              display:
                this.state.page * 15 >= this.state.totalResults ||
                this.props.pageSize * this.state.page >= 90
                  ? "none"
                  : "",
            }}
            onClick={this.loadNext}
          >
            Next&#8594;
          </button>
        </div>
      </>
    );
  }
}
