import React, { Component } from "react";
import NewsCard from "./newsCard";
import "./news.css";
import Spinner from "./spinner";
import PropTypes from "prop-types";
import InfiniteScroll from 'react-infinite-scroll-component';

export default class News extends Component {
  static defaultProps = {
    pageSize: 15,
    category: "general",
    country: "",
    language: "en",
  };
  static propTypes = {
    pageSize: PropTypes.number,
    category: PropTypes.string,
    country: PropTypes.string,
    language: PropTypes.string,
  };
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      totalResults: 0,
    };
  }

  async updateNews() {
    this.setState({
      loading: true,
    });
    let url = `https://newsapi.org/v2/top-headlines?category=${this.props.category}&country=${this.props.country}&language=${this.props.language}&apiKey=b78e87013150454abec15a569230b581&pageSize=${this.props.pageSize}&page=${this.state.page}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      loading: false,
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
    });
  }
  async componentDidMount() {
    this.setState({
      loading: true,
    });
    let url = `https://newsapi.org/v2/top-headlines?category=${this.props.category}&country=${this.props.country}&language=${this.props.language}&apiKey=b78e87013150454abec15a569230b581&pageSize=${this.props.pageSize}&page=1`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      loading: false,
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
    });
  }
  fetchMoreData =()=>{
    this.setState({
      page: this.state.page +1
    });
    this.updateNews();
  }

 render() {
    return (
      <>
      <div className="text-left mx-2">
        <small style= {{color: 'grey'}}>
          Newsy &#62; {this.props.category.charAt(0).toUpperCase()+ this.props.category.slice(1)}
        </small>
      </div>
        <div className="news_screen">

          <div className="news_container">
          <InfiniteScroll
    dataLength={this.state.articles.length}
    next={this.fetchMoreData}

    inverse={false} 
    hasMore={this.state.articles.length !== this.state.totalResults}
    loader={<Spinner/>}
    scrollableTarget="news_screen"
  >
            {!this.state.loading &&
              this.state.articles.map((element) => {
                return (
                  <NewsCard
                    title={element.title}
                    description={element.description}
                    imgSrc={element.urlToImage}
                    key={element.url}
                    newsUrl={element.url}
                    source={element.source.name}
                    author={element.author}
                    publishedDate={element.publishedAt}
                  />
                );
              })}
              </InfiniteScroll>
          </div>
        </div>
       </>
    );
  }
}
